    private class muziek_laden extends AsyncTask<Void, Void, String> {
        @Override
        protected String doInBackground(Void... params)  {

            URL url = null;
            URLConnection urlConnection = null;
            InputStream inputStream = null;

            try {
                url = new URL("http://www.radiostereo.nl/paginas/app%202.0/muziekraden.php?gebruiker="+naam+"&controleren="+controleren);
            } catch (MalformedURLException e) {
                System.out.println("MalformedURLException");
            }

            if (url != null){
                try{
                    urlConnection = url.openConnection();
                }catch (java.io.IOException e){
                    System.out.println("java.io.IOException");
                }
            }

            if (urlConnection != null){
                try{
                    inputStream = new BufferedInputStream(urlConnection.getInputStream());
                }catch (java.io.IOException e) {
                    System.out.println("java.io.IOException");
                }
            }

            if (inputStream != null){
                resultaat = inputStream.toString();

                InputStreamReader inputStreamReader = new InputStreamReader(inputStream);
                BufferedReader bufferedReader = new BufferedReader(inputStreamReader);

                try{
                    resultaat = bufferedReader.readLine();
                }catch (java.io.IOException e) {
                    System.out.println("java.io.IOException");
                }

            }else{
                resultaat = "ERROR";
            }

            return null;
        }

        @Override
        protected void onPostExecute(String result) {
            muziek_laden_klaar();
        }

    }

    public void muziek_laden_klaar(){
        controleren = "nee";
        StringTokenizer tokens = new StringTokenizer(resultaat, "|");
        artiest = tokens.nextToken();
        titel = tokens.nextToken();

        String link = "http://muziek.radiostereo.nl/"+artiest+" - "+titel+".mp3";
        link = link.replace(" ", "%20");
        new Muziekspeler().execute(link);
        new pauze().execute();
    }

    public class pauze extends AsyncTask<Void, Void, String> {
        @Override
        protected String doInBackground(Void... params)  {
            try {
                Thread.sleep(10000);
            } catch (InterruptedException e) {
                e.printStackTrace();
            }
            return null;
        }

        @Override
        protected void onPostExecute(String result) {
            Muziekspeler.stop();
        }
    }

    public void onClick(View v) {

        int id = v.getId();

        if (id == R.id.button1){
            Muziekspeler.start();
            new pauze().execute();
        }else{
            controleren = "ja";

            String artiest_gekozen = autoCompleteTextView1.getText().toString();
            String titel_gekozen = autoCompleteTextView2.getText().toString();

            Toast toast1 = Toast.makeText(getActivity().getApplicationContext(), artiest_gekozen+"|"+artiest, Toast.LENGTH_SHORT);
            toast1.show();
            Toast toast2 = Toast.makeText(getActivity().getApplicationContext(), titel_gekozen+"|"+titel, Toast.LENGTH_SHORT);
            toast2.show();

            if (artiest.equals(artiest_gekozen)){
                Toast toast = Toast.makeText(getActivity().getApplicationContext(), "Artiest JA!", Toast.LENGTH_SHORT);
                toast.show();
                score = score +1;
            }else{
                Toast toast = Toast.makeText(getActivity().getApplicationContext(), "Artiest NEE!", Toast.LENGTH_SHORT);
                toast.show();
                score = score -1;
            }

            if (titel.equals(titel_gekozen)){
                Toast toast = Toast.makeText(getActivity().getApplicationContext(), "Titel JA!", Toast.LENGTH_SHORT);
                toast.show();
                score = score +1;
            }else{
                Toast toast = Toast.makeText(getActivity().getApplicationContext(), "Titel NEE!", Toast.LENGTH_SHORT);
                toast.show();
                score = score -1;
            }

            textView.setText("Score: "+score);

            autoCompleteTextView1.setText("");
            autoCompleteTextView2.setText("");

            new muziek_laden().execute();

        }
    }

